import Link from "next/link";
import { useRouter } from "next/router";
import { Nav, Navbar, NavItem } from "react-bootstrap";
import Router from 'next/router'
const handleClickIndex = () => Router.push({pathname:'/'})
const routes = [
  { href: "/", label: "Home" },
  { href: "/about", label: "About" },
  { href: "/contact", label: "Contact" }
];
function Header() {
    const router = useRouter();
  return (
    <>
      <Nav
        className="container flex nav-fill flex-wrap flex-sm-row mx-auto"
        id="tabs-text"
        pills
        role="tablist">
            <Navbar >
            {routes.map(route => (
                <Link href={route.href}>
                              <a onClick={()=> handleClickIndex()}>
                              <img src="/ast-logo.png" alt="AST" width="50" height="40" />     
                              </a>

                </Link>              
            ))}
        </Navbar>
            <NavItem>

            </NavItem>
        <Navbar >
            {routes.map(route => (
                <li  key={route.label}>
                    <Link href={route.href}>
                    <a className={`px-3 ${router.pathname === route.href ? "text-primary-500" : ""}`}>
                        {route.label}
                    </a>
                    </Link>
                </li>
            ))}
        </Navbar>
      </Nav>
    </>
  );
}

export default Header;
