import Link from "next/link";
import { useRouter } from "next/router";
import { Nav, Navbar, NavItem } from "react-bootstrap";

const routes = [
  { href: "/", label: "Home" },
  { href: "/about", label: "About" },
  { href: "/contact", label: "Contact" }
];
function Header() {
    const router = useRouter();
  return (
    <>
      <Nav
        className="container flex nav-fill flex-wrap flex-sm-row mx-auto"
        id="tabs-text"
        pills
        role="tablist">
            <Nav>
            <Link href={route.href}>
            <a className={`px-3 ${router.pathname === route.href ? "text-primary-500" : ""}`}>
                        {route.label}
                    </a>
                    </Link>
                <img src="/ast-logo.png" alt="Girl in a jacket" width="30" height="30" />

            </Nav>
        <Navbar >
            {routes.map(route => (
                <li  key={route.label}>
                    <Link href={route.href}>
                    <a className={`px-3 ${router.pathname === route.href ? "text-primary-500" : ""}`}>
                        {route.label}
                    </a>
                    </Link>
                </li>
            ))}
        </Navbar>
      </Nav>
    </>
  );
}

export default Header;
