import Link from "next/link";
import { useRouter } from "next/router";
import {  Nav, Navbar } from "react-bootstrap";
import Router from "next/router";
import React from "react";
import ".";
const handleClickIndex = () => Router.push({ pathname: "/" });
const routes = [
  { href: "/", label: "Home" },
  { href: "/about", label: "About" },
  { href: "/contact", label: "Contact" },
];
const router = useRouter();
listener = null;
state = {
  nav: false,
};
class Header extends React.Component {
  componentDidMount() {
    window.addEventListener("scroll", this.handleScroll);
  }
  componentWillUnmount() {
    window.removeEventListener("scroll");
  }
  handleScroll = () => {
    if (window.pageYOffset > 140) {
      if (!this.state.nav) {
        this.setState({ nav: true });
      }
    } else {
      if (this.state.nav) {
        this.setState({ nav: false });
      }
    }
  };
  render() {
    return (
      <>
        <Navbar collapseOnSelect bg="light" variant="light">
          <Nav className="container flex nav-fill flex-wrap flex-sm-row mr-auto">
            <Navbar.Brand>
              <a onClick={() => handleClickIndex()}>
                <img src="/ast-logo.png" alt="AST" width="50" height="40" />
              </a>
            </Navbar.Brand>

            <Navbar className="nav-right text-uppercase ">
              {routes.map((route) => (
                <li key={route.label}>
                  <Link href={route.href}>
                    <a
                      className={`px-3 ${(router.pathname = route.href
                        ? "text-dark "
                        : "")}`}
                    >
                      {route.label}
                    </a>
                  </Link>
                </li>
              ))}
            </Navbar>
          </Nav>
        </Navbar>
      </>
    );
  }
}
export default Header;
