import Link from "next/link";
import React, { useState } from "react";
import { useRouter } from "next/router";
import { NavItem, NavLink, Nav } from "react-bootstrap";

const routes = [
  { href: "/", label: "Home" },
  { href: "/docs", label: "Documentation" },
  { href: "/contact", label: "Contact" }
];
function Header() {
    const [selected, setSelected] = React.useState("selected1");
    const [isCollapsed, setIsCollapsed] = useState(true);
    const router = useRouter();
  return (
    <>
      <Nav
        className=" nav-fill flex-column flex-sm-row"
        id="tabs-text"
        pills
        role="tablist"
      >
        <NavItem>
          <NavLink
            aria-controls="tabs-text-1"
            aria-selected={selected === "selected1"}
            className={
              "mb-sm-3 mb-md-0 " + (selected === "selected1" ? "active" : "")
            }
            data-toggle="tab"
            href="#pablo"
            id="tabs-text-1-tab"
            onClick={(e) => {
              e.preventDefault();
              setSelected("selected1");
            }}
            role="tab"
          >
            UI/UX Design
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            aria-controls="tabs-text-2"
            aria-selected={selected === "selected2"}
            className={
              "mb-sm-3 mb-md-0 " + (selected === "selected2" ? "active" : "")
            }
            data-toggle="tab"
            href="#pablo"
            id="tabs-text-2-tab"
            onClick={(e) => {
              e.preventDefault();
              setSelected("selected2");
            }}
            role="tab"
          >
            Programming
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            aria-controls="tabs-text-3"
            aria-selected={selected === "selected3"}
            className={
              "mb-sm-3 mb-md-0 " + (selected === "selected3" ? "active" : "")
            }
            data-toggle="tab"
            href="#pablo"
            id="tabs-text-3-tab"
            onClick={(e) => {
              e.preventDefault();
              setSelected("selected3");
            }}
            role="tab"
          >
            Graphic
          </NavLink>
        </NavItem>
        {routes.map(route => (
              <li className="py-1 text-left" key={route.label}>
                <Link href={route.href}>
                  <a
                    className={`px-3 font-semibold hover:opacity-75 ${
                      router.pathname === route.href ? "text-primary-500" : ""
                    }`}
                  >
                    {route.label}
                  </a>
                </Link>
              </li>
            ))}


      </Nav>
    </>
  );
}

export default Header;
